import React from 'react'
import { useState,useEffect } from 'react'
import { useParams } from 'react-router'
import './informationssection.css'
function Informationssection( props) {
    const {t,i18n} = props 
    const [detailHotel,setDetailHotel] = useState([])
    const params = useParams()
    const [loading,setLoading] = useState(true)
    const getSingleHotel = async (name) =>{
        const api = await fetch(`http://localhost:8000/api/hotels/${params.name}`)
        const data = await api.json()
        setDetailHotel(data)
        setLoading(false)
    }
    useEffect(() => {
        getSingleHotel()
    }, [params.name])
  return (
    <div className='Informationssection'>
        <div className="container">
            <div className="maindetails">
                <div className="roomdetailsnav">
                    <h3>{t('roomdetails.1')} </h3>
                    <hr className="devis" />
                    <h3>{t('location.1')}</h3>
                    <hr className="devis" />
                    <h3>{t('reviews.1')}</h3>
                   
                </div>
                <div className="information">
                    <h3>Information</h3>
                    <p></p>
                </div>
            </div>
        </div>
    </div>
  )
}

export default Informationssection
