  const [infos,setInfos] = useState([])
  useEffect( ()=> {
      getCompanyInfos()
     
    },[])

  const getCompanyInfos = async() =>{
      const info = await fetch('http://127.0.0.1:8000/api/infos')
      const data = await info.json()
      setInfos(data)}
  
   let id, email, addres, phoneNum;
      { infos.map(e => {
        return(
          {id,email,addres,phoneNum} = e
        )
    })}
