import React from 'react'
import './mainsection.css'
import { useState,useEffect } from 'react'
import right from '../assets/mainassets/right.svg'
import left from '../assets/mainassets/left.svg'
import slide1 from '../assets/mainassets/slide1.jpg'
import slide2 from '../assets/mainassets/slide2.jpg'
function MainSection() {
  const [pictures,setPictures] = useState([])
  const [current,setCurrent] = useState(0)
  const nextPicture = () => {
    setCurrent(current === 2 ? 0 : current+1)
    console.log(current)
  }
  const prevPicture = () => {
    setCurrent(current === 0 ? 2 : current-1)
    console.log(current)
  }

  useEffect(()=>{
    getPictures()
  },[])
  const getPictures = async () => {
      const allpicturs = await fetch('http://127.0.0.1:8000/api/pictures')
      const data = await allpicturs.json()
      setPictures(data)
  }
  let id,picture1,picture2,picture3;
  pictures.map((picture) => {
    return(
        {picture1,picture2,picture3} = picture
      
    )
  })
  useEffect(()=>{
     setInterval(()=>{
        nextPicture()
    },2000)
    },[])
  return (
    <div className="slider">
      <img className='arrowleft' src={left} onClick={prevPicture} alt="" width="50px" />
      <img  className='arrowright' onClick={nextPicture}  src={right} alt="" width="50px" />
      <div  className={current === 0 ? 'slider1show' : 'slider1hidde'}>
        <img className={ current == 0 ? 'slikashow' : 'slikahidde'} src={picture1} alt="" /> 
        
      </div>
      <div  className={current === 1 ? 'slider2' : 'slider2hidden'}>
        <img  className={ current == 1 ? 'slika2' : 'slika2hidden'} src={picture2} alt="" />
      </div>
      <div  className={current === 2 ? 'slider3' : 'slider3hidden'}>
        <img  className={ current == 2 ? 'slika3' : 'slika3hidden'} src={picture3} alt="" />
      </div>
      
    </div>
  )
  }

export default MainSection
