import React from 'react'
import { useState,useEffect } from 'react'
import { useParams } from 'react-router'
import './informationssection.css'
function Informationssection( props) {
    const {t,i18n} = props 
    const [detailHotel,setDetailHotel] = useState([])
    const params = useParams()
    const [loading,setLoading] = useState(true)
    const [roomactive,setRoomActive] = useState(true)
    const [locationactive,setLocationActive] = useState(false)
    const [reviewsactive,setReviewsActive] = useState(false)
    const getSingleHotel = async (name) =>{
        const api = await fetch(`http://localhost:8000/api/hotels/${params.name}`)
        const data = await api.json()
        setDetailHotel(data)
        setLoading(false)
    }
    useEffect(() => {
        getSingleHotel()
    }, [params.name])
  return (
    <div className='Informationssection'>
        <div className="container">
            <div className="maindetails">
                <div className="roomdetailsnav">
                    <h3 className={roomactive ? 'roomactive' : 'roomdeactive'}>{t('roomdetails.1')} </h3>
                    <hr className="devis" />
                    <h3 onClick={()=>setRoomActive(!roomactive)} className={roomactive ? 'roomactive' : 'roomdeactive'}>{t('location.1')}</h3>
                    <hr className="devissd" />
                    <h3>{t('reviews.1')} className={reviewsactive ? 'roomactive' : 'roomdeactive'}</h3>
                   
                </div>
                <div className="information">
                    <h3 className='informationtitle'>INFORMATION</h3>
                    <p className='descriptionhotel'>{t('dummylorem.1')}</p>
                    <hr className="devissd" />
                </div>
            </div>
        </div>
    </div>
  )
}

export default Informationssection
