import React from 'react'
import './mainsection.css'
import { useState,useEffect } from 'react'
import { Wave } from 'react-animated-text';
import 'react-calendar/dist/Calendar.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBed,faCalendarDays } from '@fortawesome/free-solid-svg-icons';
import { useTranslation } from 'react-i18next';
import Calendar from 'react-calendar'
import format from 'date-fns/format';
function MainSection() {
  const [t,i18n] = useTranslation()
  const [checkin, setCheckin] = useState(new Date());
  const [openCalendar,setOpenCalendar] = useState(false)
  const [opensdCalendar,setOpensdCalendar] = useState(false)
  const [checkout, setCheckout] = useState(new Date());

  if (openCalendar == true && opensdCalendar == true){
    setOpenCalendar(false)
    setOpensdCalendar(false)
  }

  return (
  
    <div className="slider">
      <div className="animatetext">
        <h2>Welcome to FiveStar</h2>
        
      </div>
      <h1 className='animated' id='main'> <Wave text='a stunning hotel'
      effectChange={.2}
      effect="verticalFadeIn"
      iterations={1}/></h1>
      <hr color='orange' width="20%" />
      <p style={{textAlign:'center',color:'white',fontSize:'1.2rem',lineHeight:'3rem',paddingBottom:'2rem'}}>Our hotels offer the best offer in the country, we hope you enjoy. <br /> We wish you a pleasant rest</p>
      <div className='headerSearch'>
        <div className='searchItem'>
          {openCalendar &&   <Calendar  className='calendare'   onChange={setCheckin} minDate={new Date()} 	 />}
        <label htmlFor="checkin" style={{fontSize:'1.5rem',paddingRight:'1rem'}}>Check in:</label>
        <input type="date" name='checkin' className='headerSearchIcone' onClick={() => setOpenCalendar(!openCalendar)}  onChange={setCheckin} value={format(checkin,'yyyy-MM-dd')} />
        </div>
        <div className='searchItem'>
        {opensdCalendar && <Calendar  className="calendare"  onChange={setCheckout} minDate={checkin}  />}
        <label htmlFor="checkout" style={{fontSize:'1.5rem',paddingRight:'1rem'}}>Check out:</label>
        <input type="date" name='checkout' className='headerSearchIcone' value={format(checkin,'yyyy-MM-dd')}  onClick={()=>setOpensdCalendar(!opensdCalendar) } />
        </div>
        <div className='searchItem'>
        <label htmlFor="checkout" style={{fontSize:'1.5rem',paddingRight:'1rem'}}>Number of People:</label>
          <input type="number"   />
        </div>
        
          <button className='btn' onClick={()=>handleClick('de')}>BOOK NOW</button>
         <p>{t('title.1')}</p>
      </div>
    </div>
  )
  
  }

export default MainSection
