import 'react-calendar/dist/Calendar.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBed,faCalendarDays } from '@fortawesome/free-solid-svg-icons';
import Calendar from 'react-calendar'


function MainSection() {
  const [checkin, setCheckin] = useState(new Date());
  const [checkout, setCheckout] = useState(new Date());
  const calendaropen = () => {
    return <Calendar style={{pos}}/>
  }
  return (
  
    <div className="slider">
      <div className="animatetext">
        <h2>Welcome to FiveStar</h2>
        
      </div>
      <h1 className='animated' id='main'> <Wave text='a stunning hotel'
      effectChange={.2}
      effect="verticalFadeIn"
      iterations={1}/></h1>
      <hr color='orange' width="20%" />
      <p style={{textAlign:'center',color:'white',fontSize:'1.2rem',lineHeight:'3rem',paddingBottom:'2rem'}}>Lorem ipsum dolor sit amet consectetur adipisicing elit. Ducimus id maiores soluta tempora <br />Ducimus id maiores soluta tempora</p>
      <div className='headerSearch'>
        <div className='searchItem'>
        
        <input type="date" className='headerSearchIcone' onClick={calendaropen} />
        </div>
        <div className='searchItem'>
        <input type="date" className='headerSearchIcone' />
        </div>
        <div className='searchItem'>

          <input type="number" placeholder='#numpeople' />
        </div>
        
          <button className='btn'>BOOK NOW</button>
         
      </div>
    </div>
  )
  
  }

export default MainSection
