import React from 'react'
import './mainsection.css'
import { useState,useEffect } from 'react'
import right from '../assets/mainassets/right.svg'
import left from '../assets/mainassets/left.svg'
function MainSection() {
  const [pictures,setPictures] = useState([])
  useEffect(()=>{
    getPictures()
  },[])
  const getPictures = async () => {
      const allpicturs = await fetch('http://127.0.0.1:8000/api/pictures')
      const data = await allpicturs.json()
      setPictures(data)
  }
  let id, picture1,picture2,picture3;
  {
    
    pictures.map((e) => {
      return  {id, picture1,picture2,picture3} = e
    }
    
    )
  } 
  return (
    <div className='main'>
        <img c style={{zIndex: "0", position: "absolute"}} src={picture1} width="100%" alt="" />
        <img style={{zIndex: "0", position: "absolute",opacity:'0'}} src={picture2} width="100%" alt="" />
        <img style={{zIndex: "0", position: "absolute",opacity:'0'}} src={picture3} width="100%"  alt="" />
        <div className="container">
            <img style={{zIndex: "1"}} src={left} width="50px" alt="" />
            <img style={{zIndex: "1",paddingTop:''}} src={right} width="50px" alt="" />
        </div>
       
    </div>
  )
}

export default MainSection
