import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
import { useParams } from 'react-router'
import {Link} from 'react-router-dom'
import SimpleImageSlider from "react-simple-image-slider";
import './detailssection.css'
function Detailssection(props) {
    const {t,i18n} = props 
    const [detailHotel,setDetailHotel] = useState([])
    const params = useParams()
    const getSingleHotel = async (name) =>{
        const api = await fetch(`http://localhost:8000/api/hotels/${params.name}`)
        const data = await api.json()
        setDetailHotel(data)
    }
    useEffect(() => {
        getSingleHotel()
    }, [params.name])
    const images = [
        {url:detailHotel.frimg},
        {url:detailHotel.sdimg},
        {url:detailHotel.thimg},
        {url:detailHotel.ftimg},
    
    ]
  return (
    <>
    <div className='container'>
        <div className="hotels">
            <div className="hotelsearch">
                <h2 className='smalltitle'>{detailHotel.title}</h2>
            </div>
            <div className="hotelnav">
                <Link to='/' className='tohome'>
                <h2>{t('home.1')}</h2>
                </Link>
                <h2>/</h2>
                <Link to={'../hotels'} className='tohome'>
                <h2>{t('hotelsearch.1')}</h2>
                </Link>
            </div>
        </div>
    </div>
    <div className="hoteldetilssecion">
        <div className="container">
            <div className="detailsection">
                <div className="hotelphotos">
                    <SimpleImageSlider 
                     width={896}
                     height={504}
                     images={images}
                     showBullets={true}
                     showNavs={true}
                     />
                </div>
                <div className="reservationhotel"></div>
            </div>
        </div>
    </div>
    </>
  )
}

export default Detailssection
