import React from 'react'
import './mainsection.css'
import { useState,useEffect } from 'react'
import right from '../assets/mainassets/right.svg'
import left from '../assets/mainassets/left.svg'
function MainSection() {
  const [pictures,setPictures] = useState([])
  const [current,setCurrent] = useState(0)
  
  useEffect(()=>{
    getPictures()
  },[])
  const getPictures = async () => {
      const allpicturs = await fetch('http://127.0.0.1:8000/api/pictures')
      const data = await allpicturs.json()
      setPictures(data)
  }
  const nextSlide = () => {
    setCurrent(current === 2 ? 0 : current + 1)
    console.log(current)
  }
  const prevSlide = () => {
    setCurrent(current === -1 ? 2 : current + 1)
    console.log(current)
  }
 
  return (
    <div className='slider'>
      <img className='leftarrow' onClick={nextSlide} width="50px" src={left} alt="" />
      <img className='rightarrow' onClick={nextSlide} width="50px" src={right} alt="" />
     {pictures.map((pic,index) => {
        return (
          <>
            
            <img   src={pic.picture1} className="image" alt="" />
            <img src={pic.picture2} className="image" alt="" />
            <img src={pic.picture3} className="image" alt="" />
          </>
        )
      })}       
       
    </div>
  )
  }

export default MainSection
