import React from 'react'
import './mainsection.css'
import { useState,useEffect } from 'react'
import right from '../assets/mainassets/right.svg'
import left from '../assets/mainassets/left.svg'
function MainSection() {
  const [pictures,setPictures] = useState([])
  const [current,setCurrent] = useState(0)
  let leng;
  useEffect(()=>{
    getPictures()
  },[])
  const getPictures = async () => {
      const allpicturs = await fetch('http://127.0.0.1:8000/api/pictures')
      const data = await allpicturs.json()
      setPictures(data)
  }

  return (
      pictures.map((pic,index) => {
        return (
          <div className='slider'>
          {leng = pic.length}
          <div className="pictureone">
              <img className='image' src={pic.picture1}  alt="" />
              <img src={left} alt="" width="50px" />
              <img src={right} alt="" width="50px"/>
          </div>
          <div className="picturetwo">
            <img src={pic.picture2} alt="" />
              <img src={left} alt="" width="50px" />
              <img src={right} alt="" width="50px"/>
          </div>
          <div className="picturethree" >
                <img src={pic.picture3} alt="" />
            
              <img src={left} alt="" width="50px" />
              <img src={right} alt="" width="50px"/>
          </div>
          </div>
        )
      })
  )
}

export default MainSection
